#coding:utf-8
#切割日志ip_dict = {}with open('/Users/wj/Desktop/access.txt','r') as f:    for i in f.readlines():        #print i        IpTemp = i.split(' ')        ip_dict[IpTemp[0]] = ip_dict.get(IpTemp[0],1) + 1    #print ip_dictip_count=[]for i in ip_dict.items():    ip_count.append(i)#print ip_count    #print ip_countfor j in range(len(ip_count)):    for i in range(len(ip_count)-1):        if ip_count[i][1] > ip_count[i+1][1]:            ip_count[i],ip_count[i+1] = ip_count[i+1],ip_count[i]ip_count.reverse()#写入HTML文件with open('/Users/wj/Desktop/index.html','a') as f:    f.write('<html>\n<title>\n</title>\n<body>\n\n<table border=1 >\n<table style border=1 >\n')    f.write('<tr><th>IP</th><th>次数</th></tr>')    for i in range(10):        f.write('<tr><td>%s</td><td>%s</td></tr>\n' % (ip_count[i][0], ip_count[i][1]))    f.write('</table>\n</body>\n</html>\n')